import classes from './Cart.module.css';
import Modal from '../UI/Modal'
import { useContext } from 'react';
import CartContext from '../../store/cart-context';
import CartItem from './CartItem';

const Cart = props =>{
    const cartCtx=useContext(CartContext);

    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
     const hasItems= cartCtx.items.length>0;
    const cartItemRemoveHandler = id=>{
        cartCtx.removeItem(id);
    };

    const cartItemAddHandler=item=>{
        const cartItem={...item,amount:1}
        cartCtx.addItem(cartItem);
    };
    const eventClickHandler = async() =>{
        console.log("^");
        const response = await fetch('https://react-http-8d93b-default-rtdb.firebaseio.com/food.json',
        {
            method:'POST',
            body:JSON.stringify({text:cartCtx.items}),
            headers:{
                'Content-Type':'application/json'
            }
        })

        if(!response.ok)
        {
            throw new Error('Something went wrong');
        }

        const data = response.json();
        console.log(data);
    }

  const CartItems=<ul className={classes['cart-items']}>
      {
          cartCtx.items.map(item=>{
           return <li><CartItem key={item.id}
           name={item.name}
           amount={item.amount}
           price={item.price}
           onRemove={cartItemRemoveHandler.bind(null,item.id)}
           onAdd={cartItemAddHandler.bind(null,item)}
           />
           </li>
          })
      }
  </ul> 
   
  return(
      <Modal onClose={props.onClose}>
          {CartItems}
          <div className={classes.total}>
              <span>Total Amount</span>
              <span>{totalAmount}</span>
          </div>
          <div className={classes.actions}>
              <button className={classes['button--alt']}  onClick={props.onClose}>Close</button>
              {hasItems&&<button className={classes.button} onClick={eventClickHandler}>Order</button>}
          </div>
          </Modal>

  ) 
}

export default Cart;
